import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Pressable,
  Linking,
  TouchableHighlight,
} from "react-native";
import { Dimensions } from "react-native";
import React, { useState, useEffect } from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

export default function HomeScreen() {
  const [options, setOptions] = useState([]);
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    fetch("https://ndl-be-apphanhchinh.onrender.com/features")
      .then((res) => res.json())
      .then((data) => setOptions([...data]));
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.body}>
        <View style={styles.infor}>
          <Image
            source={require("../../../../assets/images/user-image.png")}
          ></Image>
          <Text style={{ fontSize: 16, color: "#fff", width: "60%" }}>
            User's Fullname
          </Text>
          <Image source={require("../../../../assets/Icons/bell.png")}></Image>
        </View>
        <Text
          style={{
            fontSize: 18,
            fontWeight: 700,
            lineHeight: 27,
            paddingTop: 20,
            paddingBottom: 20,
          }}
        >
          Dịch Vụ Trực Tuyến
        </Text>
        {!options.length == 0 ? (
          Array.from(options).map((option, index) => {
            return (
              <View key={index} style={styles.button}>
                <View style={{ width: "30%" }}>
                  <Image
                    style={{ height: 32, width: 32 }}
                    source={{ uri: option.icon }}
                  ></Image>
                </View>
                <Text style={{ width: "70%", fontSize: 16, fontWeight: 500 }}>
                  {option.content}
                </Text>
              </View>
            );
          })
        ) : (
          <View></View>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: "100%",
    // backgroundColor: "yellow",
    justifyContent: "flex-end",
  },
  body: {
    width: "100%",
    height: "80%",
    backgroundColor: "#ffffff",
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
    position: "relative",
    paddingTop: 10,
    display: "flex",
    alignItems: "center",
  },
  infor: {
    display: "flex",
    flexDirection: "row",
    position: "absolute",
    bottom: "100%",
    paddingTop: 10,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    width: "100%",
    alignItems: "center",
    justifyContent: "space-around",
    lineHeight: 24,
  },
  button: {
    backgroundColor: "#f1f4f5",
    width: "80%",
    height: 86,
    margin: 10,
    borderRadius: 10,
    shadowColor: "#000000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.17,
    shadowRadius: 2.54,
    elevation: 3,
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    paddingLeft: 20,
    paddingRight: 20,
  },
});
