                            height: 50,
                            borderRadius: 50,
                            backgroundColor: "white",
                            display: "flex",
                            justifyContent: "center",
                            alignItems: "center",
                            marginRight: 20,
                            borderColor: "#2d5381",
                            borderWidth: 2,
                          }
