import {
  StyleSheet,
  Text,
  View,
  Image,
  Pressable,
  Alert,
  Modal,
} from "react-native";
import { Dimensions } from "react-native";
import React, { useEffect, useState } from "react";
import * as Google from "expo-auth-session/providers/google";
import * as WebBrowser from "expo-web-browser";
import "expo-dev-client";
import {
  GoogleSignin,
  GoogleSigninButton,
  statusCodes,
} from "@react-native-google-signin/google-signin";
import * as AuthSession from "expo-auth-session";

WebBrowser.maybeCompleteAuthSession();

export default function LoginScreen() {
  const [modalVisible, setModalVisible] = useState(false);
  const [message, setMessage] = useState("");
  const [bgPress, setBgPress] = useState(false);

  GoogleSignin.configure({
    scopes: ["https://www.googleapis.com/auth/drive.readonly"],
  });

  signIn = async () => {
    try {
      await GoogleSignin.hasPlayServices();
      const userInfo = await GoogleSignin.signIn();
      console.log(JSON.stringify(userInfo, null, 2));
    } catch (error) {
      if (error.code === statusCodes.SIGN_IN_CANCELLED) {
        // user cancelled the login flow
      } else if (error.code === statusCodes.IN_PROGRESS) {
        // operation (e.g. sign in) is in progress already
      } else if (error.code === statusCodes.PLAY_SERVICES_NOT_AVAILABLE) {
        // play services not available or outdated
      } else {
        // some other error happened
      }
    }
  };

  const [request, response, promtAsync] = Google.useAuthRequest({
    iosClientId:
      "230997855828-ohg62ip61177uuuaot2p17hos6scn2fl.apps.googleusercontent.com",
    webClientId:
      "230997855828-aaf8ck4prhrnttj4odo14t2qkphdind7.apps.googleusercontent.com",
    androidClientId:
      "230997855828-b0bp1l6mk8md8i4af9m7dmlrpf7jr3md.apps.googleusercontent.com",
  });

  useEffect(() => {
    if (response?.type === "success") {
      fetch("https://www.googleapis.com/userinfo/v2/me", {
        headers: {
          Authorization: `Bearer ${response.authentication.accessToken}`,
        },
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.hd != "fpt.edu.vn") {
            setModalVisible(true);
            setMessage("Vui lòng sử dụng email của FPT POLYTECHNIC !");
          } else {
            const data1 = {
              googleID: data.id,
              email: data.email,
              name: data.name,
              imageURL: data.picture,
            };
            fetch("https://ndl-be-apphanhchinh.onrender.com/authentication", {
              method: "POST",
              headers: {
                Accept: "application.json",
                "Content-Type": "application/json",
              },
              body: JSON.stringify(data1),
            })
              .then((res) => res.json())
              .then((data) => {
                console.log(data);
                // AsyncStorage.setItem("accessToken", data.accessToken);
              })
              .catch((err) => console.log(err));
          }
        });
    }
  }, [response]);

  return (
    <View style={styles.container}>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>{message}</Text>
            <Pressable
              style={[styles.button, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>ĐÓNG</Text>
            </Pressable>
          </View>
        </View>
      </Modal>

      <View style={styles.body}>
        <Image
          style={styles.logo}
          source={{ uri: "https://i.ibb.co/LQ0mhhR/logo.png" }}
        ></Image>
        <Pressable style={styles.button}>
          <Text>Lựa chọn cơ sở</Text>
        </Pressable>
        <Pressable
          onPress={() => promtAsync({ useProxy: false, showInRecents: true })}
          style={bgPress == false ? styles.buttonLogin : styles.buttonPress}
        >
          <Image
            style={{ width: 20, height: 20 }}
            source={require("../../../assets/Icons/google.png")}
          ></Image>
          <Text style={styles.buttonText}>Google</Text>
        </Pressable>
        <GoogleSigninButton
          size={GoogleSigninButton.Size.Wide}
          color={GoogleSigninButton.Color.Dark}
          onPress={signIn}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    position: "relative",
  },
  body: {
    backgroundColor: "#ffffff",
    width: "80%",
    height: "70%",
    borderRadius: 20,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-around",
    paddingTop: 40,
    paddingLeft: 40,
    paddingRight: 40,
    paddingBottom: 40,
    alignSelf: "center",
  },
  logo: {
    width: 150,
    height: 50,
    resizeMode: "cover",
  },
  button: {
    backgroundColor: "red",
    paddingTop: 10,
    paddingBottom: 10,
    width: "100%",
    borderRadius: 5,
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
  },
  buttonLogin: {
    flexDirection: "row",
    backgroundColor: "#3257c6",
    paddingTop: 10,
    paddingBottom: 10,
    width: "100%",
    borderRadius: 5,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  buttonPress: {
    flexDirection: "row",
    backgroundColor: "grey",
    paddingTop: 10,
    paddingBottom: 10,
    width: "100%",
    borderRadius: 5,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  buttonText: {
    color: "white",
    marginLeft: 10,
  },
  webview: {
    position: "absolute",
    height: parseFloat(Dimensions.get("window").height) * (100 / 100),
    width: parseFloat(Dimensions.get("window").width) * (100 / 100),
    backgroundColor: "red",
    top: 0,
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});
